    description: "environment variables test",
    tags: [
        "tag1",
        "tag2",
    ],
    namespaceId: mainContainerNamespace.id,
    registryImage: pulumi.interpolate`${mainContainerNamespace.registryEndpoint}/alpine:test`,
    port: 9997,
