func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		cfg := config.New(ctx, "")
		userData := map[string]interface{}{
			"cloud-init": "#cloud-config\napt-update: true\napt-upgrade: true\n",
			"foo":        "bar",
		}
		if param := cfg.GetObject("userData"); param != nil {
			userData = param
		}
