package generated_program;

import com.pulumi.Context;
import com.pulumi.Pulumi;
import com.pulumi.core.Output;
import io.dirien.scaleway.MnqSns;
import io.dirien.scaleway.MnqSnsCredentials;
import io.dirien.scaleway.MnqSnsCredentialsArgs;
import com.pulumi.scaleway.inputs.MnqSnsCredentialsPermissionsArgs;
import io.dirien.scaleway.MnqSnsTopic;
import io.dirien.scaleway.MnqSnsTopicArgs;
import io.dirien.scaleway.TemDomain;
import io.dirien.scaleway.TemDomainArgs;
import io.dirien.scaleway.DomainRecord;
import io.dirien.scaleway.DomainRecordArgs;
import io.dirien.scaleway.TemDomainValidation;
import io.dirien.scaleway.TemDomainValidationArgs;
import io.dirien.scaleway.TemWebhook;
import io.dirien.scaleway.TemWebhookArgs;
import com.pulumi.resources.CustomResourceOptions;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        final var config = ctx.config();
        final var domainName = config.get("domainName");
        var sns = new MnqSns("sns");

        var snsCredentials = new MnqSnsCredentials("snsCredentials", MnqSnsCredentialsArgs.builder()
            .permissions(MnqSnsCredentialsPermissionsArgs.builder()
                .canManage(true)
                .build())
            .build());

        var snsTopic = new MnqSnsTopic("snsTopic", MnqSnsTopicArgs.builder()
            .accessKey(snsCredentials.accessKey())
            .secretKey(snsCredentials.secretKey())
            .build());

        var cr01 = new TemDomain("cr01", TemDomainArgs.builder()
            .acceptTos(true)
            .build());

        var spf = new DomainRecord("spf", DomainRecordArgs.builder()
            .dnsZone(domainName)
            .type("TXT")
            .data(cr01.spfConfig().applyValue(_spfConfig -> String.format("v=spf1 %s -all", _spfConfig)))
            .build());

        var dkim = new DomainRecord("dkim", DomainRecordArgs.builder()
            .dnsZone(domainName)
            .type("TXT")
            .data(cr01.dkimConfig())
            .build());

        var mx = new DomainRecord("mx", DomainRecordArgs.builder()
            .dnsZone(domainName)
            .type("MX")
            .data(".")
            .build());

        var dmarc = new DomainRecord("dmarc", DomainRecordArgs.builder()
            .dnsZone(domainName)
            .type("TXT")
            .data(cr01.dmarcConfig())
            .build());

        var valid = new TemDomainValidation("valid", TemDomainValidationArgs.builder()
            .domainId(cr01.id())
            .region(cr01.region())
            .timeout(3600)
            .build());

        var webhook = new TemWebhook("webhook", TemWebhookArgs.builder()
            .domainId(cr01.id())
            .eventTypes(            
                "email_delivered",
                "email_bounced")
            .snsArn(snsTopic.arn())
            .build(), CustomResourceOptions.builder()
                .dependsOn(                
                    valid,
                    snsTopic)
                .build());

    }
}
