		if err != nil {
			return err
		}
		_, err = scaleway.LookupSecret(ctx, &scaleway.LookupSecretArgs{
			Name: pulumi.StringRef("your_secret_name"),
		}, nil)
		if err != nil {
			return err
		}
		return nil
	})
}
